import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { Box, Typography, Card, CardContent, Divider, List, ListItem, ListItemIcon, ListItemText } from '@mui/material';
import LocalActivityIcon from '@mui/icons-material/LocalActivity';
import RestaurantIcon from '@mui/icons-material/Restaurant';
import InfoIcon from '@mui/icons-material/Info';
import AttachMoneyIcon from '@mui/icons-material/AttachMoney';
import { APIProvider, Map, Marker } from '@vis.gl/react-google-maps';
import axios from 'axios';

// Helper to get coordinates from place details (dummy fallback if not available)
const getCoords = (place: any) => {
  if (place && place.geometry && place.geometry.location) {
    return {
      lat: place.geometry.location.lat,
      lng: place.geometry.location.lng,
    };
  }
  return { lat: 13.7563, lng: 100.5018 };
};

// Component for a single day's itinerary
const DailyItineraryCard = ({ dayData }: { dayData: any }) => (
  <Card sx={{ mb: 4, boxShadow: 3, borderRadius: 2 }}>
    <CardContent>
      <Typography variant="h5" color="primary" fontWeight="bold" sx={{ mb: 2 }}>
        Day {dayData.day}
      </Typography>
      <Divider sx={{ mb: 2 }} />
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
        <LocalActivityIcon color="primary" sx={{ mr: 1 }} />
        <Typography variant="subtitle1" fontWeight="bold">Activities</Typography>
      </Box>
      <List>
        {dayData.activities?.map((activity: any, i: number) => (
          <ListItem key={i} disableGutters sx={{ mb: 1 }}>
            <ListItemText
              primary={activity}
              sx={{ ml: 2 }}
            />
          </ListItem>
        ))}
      </List>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 1, mt: 2 }}>
        <RestaurantIcon color="success" sx={{ mr: 1 }} />
        <Typography variant="subtitle1" fontWeight="bold">Food Recommendations</Typography>
      </Box>
      <List>
        {dayData.food_recommendations?.map((food: any, i: number) => (
          <ListItem key={i} disableGutters sx={{ mb: 1 }}>
            <ListItemText
              primary={food}
              sx={{ ml: 2 }}
            />
          </ListItem>
        ))}
      </List>
    </CardContent>
  </Card>
);

const ItineraryPage: React.FC = () => {
  const location = useLocation();
  const [mapsApiKey, setMapsApiKey] = useState<string>('');
  
  useEffect(() => {
    axios.get('http://localhost:8000/config/maps-key')
      .then(res => setMapsApiKey(res.data.mapsApiKey))
      .catch(err => console.error("Failed to fetch maps API key", err));
  }, []);

  let itineraryData = location.state?.itinerary?.itinerary;

  // Check if the itinerary data is a JSON string and parse it
  if (typeof itineraryData === 'string') {
    try {
      itineraryData = JSON.parse(itineraryData);
    } catch (e) {
      console.error("Failed to parse itinerary data string", e);
      itineraryData = null;
    }
  }

  if (!itineraryData) {
    return <Typography variant="h6" color="error" sx={{ textAlign: 'center', mt: 4 }}>No itinerary data found.</Typography>;
  }

  // Collect all places for markers
  const allPlaces: any[] = [];
  itineraryData.days?.forEach((day: any) => {
    day.activities?.forEach((activity: any) => {
      activity.places?.forEach((place: any) => {
        allPlaces.push(place);
      });
    });
    day.food_recommendations?.forEach((food: any) => {
      food.places?.forEach((place: any) => {
        allPlaces.push(place);
      });
    });
  });

  // Center map on first place or fallback
  const center = allPlaces.length > 0 ? getCoords(allPlaces[0]) : { lat: 13.7563, lng: 100.5018 };

  return (
    <Box sx={{ maxWidth: 900, mx: 'auto', p: 3 }}>
      <Typography variant="h3" fontWeight="bold" align="center" sx={{ my: 4, color: 'primary.main' }}>
        Trip Itinerary ✈️
      </Typography>
      
      {/* Trip Summary */}
      {itineraryData.trip_summary && (
        <Card sx={{ mb: 4, boxShadow: 3, borderRadius: 2 }}>
          <CardContent>
            <Typography variant="h6" fontWeight="bold" sx={{ mb: 1 }}>Trip Summary</Typography>
            <Typography variant="body1" sx={{ whiteSpace: 'pre-line' }}>
              {itineraryData.trip_summary}
            </Typography>
          </CardContent>
        </Card>
      )}

      {/* Daily Itinerary Cards */}
      {itineraryData.days?.map((day: any, idx: number) => (
        <DailyItineraryCard key={idx} dayData={day} />
      ))}

      {/* Budget Breakdown and Notes */}
      <Box sx={{ display: 'grid', gap: 3, gridTemplateColumns: { xs: '1fr', sm: '1fr 1fr' }, my: 4 }}>
        {itineraryData.budget_breakdown && (
          <Card sx={{ boxShadow: 3, borderRadius: 2 }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <AttachMoneyIcon color="primary" sx={{ mr: 1 }} />
                <Typography variant="h6" fontWeight="bold">Budget Breakdown</Typography>
              </Box>
              <List>
                {Object.entries(itineraryData.budget_breakdown).map(([key, value]) => (
                  <ListItem key={key} disableGutters>
                    <ListItemText primary={`${key}: ${value}`} sx={{ ml: 2 }} />
                  </ListItem>
                ))}
              </List>
            </CardContent>
          </Card>
        )}
        {itineraryData.notes && (
          <Card sx={{ boxShadow: 3, borderRadius: 2 }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <InfoIcon color="warning" sx={{ mr: 1 }} />
                <Typography variant="h6" fontWeight="bold">Notes</Typography>
              </Box>
              <List>
                {itineraryData.notes.map((note: string, i: number) => (
                  <ListItem key={i} disableGutters>
                    <ListItemText primary={note} sx={{ ml: 2 }} />
                  </ListItem>
                ))}
              </List>
            </CardContent>
          </Card>
        )}
      </Box>
      
      {/* Google Map with markers */}
      <Box sx={{ mt: 6, height: 400, boxShadow: 3, borderRadius: 2, overflow: 'hidden' }}>
        {mapsApiKey && (
          <APIProvider apiKey={mapsApiKey}>
            <Map center={center} zoom={12} style={{ width: '100%', height: '100%' }}>
              {allPlaces.map((place, idx) => {
                const coords = getCoords(place);
                return <Marker key={idx} position={coords} title={place.name} />;
              })}
            </Map>
          </APIProvider>
        )}
      </Box>
    </Box>
  );
};

export default ItineraryPage;
